'use strict';

exports.register = function (server, options, next) {
    server.route({
        method: 'GET',
        path: '/plugin1',
        handler: function (request, reply) {
            reply('plugin 1 test passed');
        }
    });

    next();
};

exports.register.attributes = {
    name: 'test-plugin-1',
    version: '1.0.0'
};
